Value.value:
@inherits RazorFunction

@functions {
    public override string FunctionDescription
    {
        get  { return "A demo function that outputs a hello message."; }
    }

    [FunctionParameter(Label = "Product List Page")]
    public DataReference<IPage> ProductListPage { get; set; }

}


<html xmlns="http://www.w3.org/1999/xhtml">
<head>
</head>
<body>
    <div>
        @{var categories = Data.Get<My.Sale.Category>().Where(t => t.Parent == Guid.Empty).ToList(); }
        @RecursiveCategory(categories)
    </div>
</body>
</html>

@helper RecursiveCategory(List<My.Sale.Category> categories)
{
    using (DataConnection Data = new DataConnection())
    {
		<div class="leftBoxContainer" id="categories">
		<div class="sidebox-header-left main-sidebox-header-left">
			<h3 class="leftBoxHeading main-sidebox-header-right" id="categoriesHeading">Categories</h3>
		</div>
		<div id="categoriesContent" class="sideBoxContent">
			<div class="categories-top-list no-dots"><a class="category-top" href="#=index&amp;cPath=1">Hardware-&gt;</a><span class="sub-count"> (6)</span></div>
			<div class="categories-top-list "><a class="category-top" href="#">Software-&gt;</a><span class="sub-count"> (4)</span></div>
			<div class="categories-top-list "><a class="category-top" href="#">DVD Movies-&gt;</a><span class="sub-count"> (17)</span></div>
			<div class="categories-top-list "><a class="category-top" href="#">Gift Certificates</a><span class="sub-count"> (6)</span></div>
			<div class="categories-top-list "><a class="category-top" href="#">Big Linked</a><span class="sub-count"> (24)</span></div>
			<div class="categories-top-list "><a class="category-top" href="#">Test Examples</a><span class="sub-count"> (16)</span></div>
			<div class="categories-top-list "><a class="category-top" href="#">Free Call Stuff</a><span class="sub-count"> (7)</span></div>
			<div class="categories-top-list "><a class="category-top" href="#">Music</a><span class="sub-count"> (1)</span></div>
		</div>
		</div>
        <ul>
        @foreach (var item in categories)
        {
            <li><a href='@Html.C1().PageUrl(@ProductListPage.Data)/@item.Id'>@(item.CategoryName)</a>
            @{
                var subCategories = Data.Get<My.Sale.Category>().Where(t => t.Parent == item.Id).ToList();
                if (subCategories.Count > 0)
                {
                    @RecursiveCategory(subCategories);
                }
            }
            </li>
        }
        </ul>
    }
}
